shaders.var:

uniform highp mat4 inWorldViewProjMatrix;
uniform highp vec3 inTextureMatrix0[2];

attribute vec3 inPos;
attribute vec2 inUV0;

varying highp vec2 uv;

void main()
{
	gl_Position = inWorldViewProjMatrix * vec4(inPos, 1.0);
	uv = vec2(dot(inTextureMatrix0[0], vec3(inUV0, 1.)), dot(inTextureMatrix0[1], vec3(inUV0, 1.)));
}


varying highp vec2 uv;
uniform sampler2D texture0;

void main()
{
	lowp vec4 texColor0 = texture2D(texture0, uv);
	gl_FragColor = texColor0;
}
