shaders.var:

#version 330

#define POSITION 	0
#define NORMAL 		2
#define TEXCOORD0 	5

layout (location = POSITION)  in vec3 positions;
layout (location = TEXCOORD0) in vec2 texture0;

struct Transform
{
	mat4 modelMatrix;
	mat4 viewMatrix;
	mat4 projectionMatrix;
};

uniform Transform 	transform;

struct Vertex 
{
	vec2  texCoord0;
};

out Vertex fragVertex;

void main()
{
	//fragVertex.texCoord0 = texture0;
	//gl_Position = vec4(texture0.xy, 0, 1);

	vec4 vertex   = transform.modelMatrix * vec4(positions, 1.0);
	fragVertex.texCoord0 = texture0;

	gl_Position = transform.projectionMatrix * transform.viewMatrix * vertex;

	//vec4 vertex   = transform.modelMatrix * vec4(positions.xy, 0.0, 1.0);
	//gl_Position = transform.orthoMatrix * vertex;
}


#version 330

uniform sampler2D sampler0;

struct Vertex 
{
	vec2  texCoord0;
};

in 	Vertex 	fragVertex;
out vec4 	fragColor;

void main()
{
	//vec4 color = vec4(1.0, 1.0, 1.0, 1.0);
	//vec4 texColor = texture(sampler0, fragVertex.texCoord0);
	//fragColor = texColor.rrrr * color;

	fragColor = texture(sampler0, fragVertex.texCoord0);
}
