shaders.var:

#version 330
#define POSITION 	0

layout (location = POSITION) in vec3 positions;

struct Transform
{
	mat4 projectionMatrix;
};

uniform Transform transform;

void main()
{
	gl_Position = transform.projectionMatrix * vec4(positions, 1.0);
}


#version 330
out vec4 fragColor;

void main()
{
	fragColor = vec4(1.0f, 1.0f, 1.0f, 1.0f);
}
