shaders.var:

					#version 330
					#define POSITION 	0

					layout (location = POSITION) in vec3 positions;

					struct Transform
					{
						mat4 orthoMatrix;
						mat4 modelMatrix;
					};
					uniform Transform transform;

					void main()
                    {
                        vec4 vertex = transform.modelMatrix * vec4(positions, 1.0);
                        gl_Position = transform.orthoMatrix * vertex;
					}


#version 330
out vec4 fragColor;

void main()
{
	fragColor = vec4(1.0f, 1.0f, 1.0f, 1.0f);
}
